);

INSERT INTO #VLFInfo
EXEC sp_executesql N'DBCC LOGINFO() WITH NO_INFOMSGS';

SELECT CASE 
WHEN COUNT(*) > 50 THEN 1
ELSE 0
END
FROM #VLFInfo;

